function getErrorMessage(n){if(n.responseText&&n.responseText!=="")return responseText;switch(n.status){case 400:return"你提供的信息无效，请修改后再试一次。如果多次发生该错误，请联系管理员。";case 401:case 403:return"你没有权限执行该操作。请先登录或使用具有权限的账号重新登录。";default:return"目前无法执行该操作，请稍后再试一次。如果多次发生该错误，请联系管理员。"}}function handleAjaxFailure(n,t){$(t).text(getErrorMessage(n))}